import React, { useState } from 'react';
import botImage from '../assets/maya-bot.png'; // AsegÃºrate de tener esta imagen en tu carpeta assets

export default function MayaBot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const sendMessage = (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    setMessages([...messages, { from: 'user', text: input }]);
    // AquÃ­ puedes integrar con backend, ChatGPT, etc.
    setInput('');
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-white to-purple-100 px-4 py-8">
      <img src={botImage} alt="Maya Bot" className="w-32 h-32 mb-4 rounded-xl shadow-lg" />

      <h1 className="text-3xl font-bold text-gray-800 mb-6">Maya Bot</h1>

      <div className="w-full max-w-md bg-white rounded-xl shadow-lg p-4 space-y-4">
        <div className="h-64 overflow-y-auto space-y-2">
          {messages.map((msg, i) => (
            <div
              key={i}
              className={`p-2 rounded-lg max-w-xs ${
                msg.from === 'user'
                  ? 'bg-purple-200 self-end ml-auto'
                  : 'bg-gray-200 self-start'
              }`}
            >
              {msg.text}
            </div>
          ))}
        </div>

        <form onSubmit={sendMessage} className="flex gap-2">
          <input
            type="text"
            className="flex-grow border border-gray-300 rounded-lg px-3 py-2"
            placeholder="PregÃºntame algo..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button
            type="submit"
            className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700"
          >
            Enviar
          </button>
        </form>
      </div>
    </div>
  );
}
